import './hero.css'
import arrow_btn from "../../assets/arrow_btn.png"
import pause_icon from '../../assets/pause_icon.png'
import play_icon from '../../assets/play_icon.png'
export const Hero = ({heroData, setHeroData, heroCount, setPlayStatus, playStatus }) => {
  return (
    <div className='hero'>
      <div className="hero-text">
      <p>{heroData.text1}</p>
      <p>{heroData.text2}</p>
      </div>
      
      
    </div>
  )
}
export default Hero;
