// Change 'background' to 'Background'
import './background.css';
import video1 from '../../assets/video1.mp4';
import image1 from '../../assets/image1.png';
import image2 from '../../assets/image2.png';
import image3 from '../../assets/image3.png';

// Correct component name with an uppercase letter
export const Background = ({ playStatus, heroCount }) => {
  if (playStatus) {
    return (
      <video className="background" autoPlay loop muted>
        <source src={video1} type="video/mp4" />
      </video>
    );
  } else if (heroCount === 0) {
    return <img src={image1} className="background" alt="1" />;
  } else if (heroCount === 1) {
    return <img src={image2} className="background" alt="2" />;
  } else if (heroCount === 2) {
    return <img src={image3} className="background" alt="3" />;
  }
  return null; // Fallback in case none of the conditions match
};

export default Background;
